<?php 

require 'mailgun-php/vendor/autoload.php';
use Mailgun\Mailgun;

function sendContact($email, $text) {
  $mg = new Mailgun(MAILGUN_KEY);
  $domain = MAILGUN_DOMAIN;

  $result = $mg->sendMessage($domain, array(
    'from'    => 'Morning Chalk Up Ambassadors <info@mail.morningchalkup.com>',
    'h:Reply-To' => $email,
    'to'      => 'Morning Chalk Up <info@morningchalkup.com>',
    'subject' => 'Ambassador Question: ' . $email,
    'text'    => $text,
  ));
}

function sendLevelUpdate($aid, $sid) {
  global $con;

  $mg = new Mailgun(MAILGUN_KEY);
  $domain = MAILGUN_DOMAIN;

  $u = $con->fetch("SELECT * FROM cu_amb_usr WHERE aid = ?", $aid);

  $status = $con->fetch("SELECT * FROM cu_amb_status WHERE sid = ?", $sid);

  $to = $u['fullname'] . '<' . $u['email'] . '>';

  $html = "<p>Congradulations on your new status: " . $status['status'] . "</p>"

  $html = getTemplateTop() . $html . getTemplateBottom();

  $result = $mg->sendMessage($domain, array(
    'from'    => 'Morning Chalk Up Ambassadors <info@mail.morningchalkup.com>',
    'h:Reply-To' => 'Morning Chalk Up <info@morningchalkup.com>',
    'to'      => $to,
    'subject' => 'You\'ve Leveld Up to ' . $status['status'] . '!',
    'html'    => $html,
  ));


}

function sendPasswordReset($aid,$token,$site) {
  global $con;

  $mg = new Mailgun(MAILGUN_KEY);
  $domain = MAILGUN_DOMAIN;

  $u = $con->fetch("SELECT * FROM cu_amb_usr WHERE aid = ?", $aid);

  $to = $u['fullname'] . '<' . $u['email'] . '>';

  $link = $site . '/reset/?token=' . $token;

  $html = "<p>We have received a request to reset your password on Morning Chalk Up Ambassadors for username <strong>" . $u['username'] . "</strong>. If you did not make this request feel free to ignore it. If you did make this request your reset will expire in 24 hours.</p>";

  $html .= '<p><a href="' . $link . '">Reset your password.</a></p>';

  $html = getTemplateTop() . $html . getTemplateBottom();

  $result = $mg->sendMessage($domain, array(
    'from'    => 'Morning Chalk Up Ambassadors <info@mail.morningchalkup.com>',
    'h:Reply-To' => 'Morning Chalk Up <info@morningchalkup.com>',
    'to'      => $to,
    'subject' => 'Password Reset Morning Chalk Up Ambassadors',
    'html'    => $html,
  ));
}

